  width: 100%;
  height: 1000px;
  background-color: blue;
  font-family: Roboto,Arial,sans-serif;
`;

const CalendarHeader = styled.div`
  position: fixed;
  display: flex;
  flex-flow: column;
  width: 100%;
  height: 100px;
  top: 0;
  padding: 40px 0px 0px 0px;
  background-color: white;
`;

