  color: #2e2e2e;
  font-size: 14px;
  margin: 0px;
`;

const Date = styled.p`
  font-size:36px;
  margin: 0px;
`;

const HoursContainer = styled.div`
  margin-top: 150px;
`;

const Calendar = () => {
  let hours = [];
  let hour = "";
  for (let i = 1; i < 25; ++i) {
    if (i < 13){ 
      hour = `${i} am`;
    } else {
      hour = `${i -12} pm`;     
    } 
    hours.push(<Hour value={hour} />)
  }

  return (
    <Container> 
      <CalendarHeader>
