import React, { useState, useContext } from 'react';
import styled from 'styled-components';

const GroupRadio = (props) => {
  const [clicked, click] = useState(false);

  const GroupElement = styled.input`
    position: relative;
    display: block;
    label {
      display: block;
      background: lighten($accentcolor, 30%);
      color: $darkcolor;
      border-radius: 5px;
      padding: 10px 20px;
      border: 2px solid lighten($accentcolor, 20%);
      margin-bottom: 5px;
      cursor: pointer;
      &:after,
      &:before {
        content: "";
        position: absolute;
        right: 11px;
        top: 11px;
        width: 20px;
        height: 20px;
        border-radius: 3px;
        background: lighten($accentcolor, 15%);
      }
      &:before {
        background: transparent;
        transition: 0.1s width cubic-bezier(0.075, 0.82, 0.165, 1) 0s,
          0.3s height cubic-bezier(0.075, 0.82, 0.165, 2) 0.1s;
        z-index: 2;
        overflow: hidden;
        background-repeat: no-repeat;
        background-size: 13px;
        background-position: center;
        width: 0;
        height: 0;
        background-image: url(data:image/svg+xml;base64,);
      }
    }    
  `;

  const changeColor = () => {
    clicked ? click(false) : click(true);
  }

  return (
    <GroupElement onClick={changeColor} type="radio" name="CroupElement" value={props.value} />
  );
}

export default GroupRadio;
