import React, { useState, useContext } from 'react';
import styled from 'styled-components';

const GroupRadio = (props) => {
  const [clicked, click] = useState(false);

  const GroupElement = styled.input`

  `;

  const StyledLabel = styled.label`
    background-color: ${ clicked ? '#8D7AFF' : 'white' };
    border: solid 1px #c9c9c9;
    width: 350px;
    height: 30px;
    margin: 10px 0px;
    padding: 13px 0 0 20px;
    color: ${ clicked ? 'white' : '#363636' };
  `;

  const changeColor = () => {
    clicked ? click(false) : click(true);
  }

  return (
    <div>
      <GroupElement onClick={changeColor} type="radio" name="CroupElement" value={props.value} />
      <label for="dewey">Dewey</label>
    </div>
  );
}

export default GroupRadio;
