import React, { useState, useContext } from 'react';
import styled from 'styled-components';

const GroupRadio = (props) => {
  const [clicked, click] = useState(false);

  const GroupElement = styled.div`
    background-color: ${ clicked ? '#8D7AFF' : 'white' };
    border: solid 1px #c9c9c9;
    width: 350px;
    height: 40px;
    margin: 10px 0px;
    padding: 0px 0px 0px 20px;
    color: ${ clicked ? 'white' : '#363636' };
  `;

  const Span = styled.span`
    display:inline-block;
    vertical-align:middle;
    line-height:normal;
  `;

  const changeColor = () => {
    clicked ? click(false) : click(true);
  }

  return (
    <GroupElement onClick={changeColor}><Span>{props.value}</Span></GroupElement>
  );
}

export default GroupRadio;
