import React, { useState } from 'react';
import styled from 'styled-components';

const HourContainer = styled.div`   
  display: grid;
  color: #5c5c5c;
  font-size: 14px;
  height: 60px;
  grid-template-columns: 6% 1% 93%;
  grid-template-rows: 50% 50%;
`;

const HourElement = styled.div`   
  text-align: center;
  padding: 20px 0px 0px 25px;
  grid-row-start: 1;
  grid-row-end: 3;
  `;

const HourNotes = styled.div`   
  border-bottom: solid 1px #c8c8c8;
`;

const HourLineTop = styled.div`
  border-bottom: solid 1px #c8c8c8;
  border-right: solid 1px #c8c8c8;

`;

const HourLineBottom = styled.div`
  border-right: solid 1px #c8c8c8;
  grid-row-start: 2;
`;

const Note = styled.div`
  border-radius: 5px;
  background-color: blue;
  grid-row-start: 2;
  grid-column-start: 3;
  height: 10px;
`;

const Hour = (props) => {
  let note;

  const setNote = () => {
    note = <Note>awlfkapfawpj</Note>
  }

  return (
    <HourContainer>
      <HourElement>{props.value}</HourElement>
      <HourLineTop />
      <HourLineBottom />
      <HourNotes onClick = { setNote }></HourNotes>
      {note}
    </HourContainer>
  );
}

export default Hour;
